import { useDispatch } from 'react-redux';
import { useRef } from 'react';

import classes from './Auth.module.css';
import { authActions } from '../store/auth';

const Auth = () => {

  let email = 'user@gmail.com'
  let password = 'user@123'
  const emailInputRef=useRef();
  const passwordInputRef=useRef()

  const enteredEmail = emailInputRef.current.value;
  const enteredPassword = passwordInputRef.current.value;

  const dispatch = useDispatch();

  const loginHandler = (event) => {
    event.preventDefault();
if(enteredEmail === email && enteredPassword === passwordInputRef){
    dispatch(authActions.login());
}else{
  let message = 'Check User mail Id and Password'
}
  };

  return (
    <main className={classes.auth}>
      <section>
        <form onSubmit={loginHandler}>
          <div className={classes.control}>
            <label htmlFor='email'>Email</label>
            <input type='email' ref={emailInputRef} id='email' />
          </div>
          <div className={classes.control}>
            <label htmlFor='password'>Password</label>
            <input type='password'ref={passwordInputRef}  id='password' />
          </div>
          <button>Login</button>
        </form>
      </section>
    </main>
  );
};

export default Auth;
